// Text: http://adventofcode.com/2016/day/2
// Input: http://adventofcode.com/2016/day/2/input
const input = 
`



`
;

const keypad = [
	[1,2,3], 
	[4,5,6], 
	[7,8,9]
];

// starting position is key 5
let x = 1;
let y = 1;

// Polyfill Math.clamp:
if( typeof Math.clamp !== 'function' ) {
	Math.clamp = (p,min,max) => Math.max(min,Math.min(max,p));
}

const instructions = input.split('\n');
const code = instructions.map(line => {
	line.split('').map(char => {
		switch(char) {
			case 'U':
				y--;
				break;
			case 'D':
				y++;
				break;
			case 'L':
				x--;
				break;
			case 'R':
				x++;
				break;
		}
		// clamp coordinates
		x = Math.clamp(x, 0, 2);
		y = Math.clamp(y, 0, 2);
	});
	return keypad[y][x];
}).join('');

console.log('Part 1 solution:', code);
